        protected void aktif(){
        tkodetabungan.setEnabled(true);
        tnik.setEnabled(true);
        tnama.setEnabled(true);
        tsaldoawal.setEnabled(true);
        ttanggal.setEnabled(true);
        tsaldokeluar.setEnabled(true);
        tsaldo.setEnabled(true);
        tkodetabungan.requestFocus(true);
    }
            
     protected void kosong(){
         tkodetabungan.setText("");
         tnik.setText("");
         tnama.setText("");
         tsaldoawal.setText("");
         ttanggal.setText("");
         tsaldokeluar.setText("");
         tsaldo.setText("");
     }
    
     protected void datatable(){
         Object[] Baris ={"kode_tabungan","nik","nama","saldo_awal","tanggal","saldo_keluar","saldo"};
         tableModel = new DefaultTableModel (null, Baris);
         tblpenarikan.setModel(tableModel);
         String sql = "select * from simpanan";
         try{
            java.sql.Statement stat=conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
             String a = hasil.getString("kode_tabungan");
             String b = hasil.getString("nik");
             String c = hasil.getString("nama");
             String d = hasil.getString("saldo");
             String e = hasil.getString("tanggal");
             String f = hasil.getString("tabungan_keluar");
             String g = hasil.getString("saldo");
             
             String[] data={a,b,c,d,e,f,g};
             tableModel.addRow(data);
            }   
         }catch(Exception e){
         
         }
   
     }
