    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        String sql="insert into simpanan value(?,?,?,?,?,?,?,?)";
        try{
            PreparedStatement stat=conn.prepareStatement(sql);
            stat.setString(1, tkodetabungan.getText());
            stat.setString(2, tnik.getText());
            stat.setString(3, tnama.getText());
            stat.setString(4, tsaldoawal.getText());
            stat.setString(5, ttanggal.getText());
            stat.setString(6, "0");
            stat.setString(7, tsaldokeluar.getText());
            stat.setString(8, tsaldo.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data BERHASIL disimpan");
            kosong();
            tkodetabungan.requestFocus();
            datatable();
            
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data GAGAL disimpan");
        }
    }//GEN-LAST:event_bsimpanActionPerformed

    private void bubahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bubahActionPerformed
        try{
            String sql="UPDATE simpanan set nik=?, nama=?, saldo_sekarang=?, tanggal=?, tabungan_masuk=?, tabungan_keluar=?, saldo=? where kode_tabungan=?";
            PreparedStatement stat= conn.prepareStatement(sql);
            
            
            stat.setString(1, tnik.getText());
            stat.setString(2, tnama.getText());
            stat.setString(3, tsaldoawal.getText());
            stat.setString(4, ttanggal.getText());
            stat.setString(5, "0");
            stat.setString(6, tsaldokeluar.getText());
            stat.setString(7, tsaldo.getText());
            stat.setString(8, tkodetabungan.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            tkodetabungan.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }
    }//GEN-LAST:event_bubahActionPerformed

    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        try{
            String sql="DELETE from simpanan where kode_tabungan=?";
            PreparedStatement stat= conn.prepareStatement(sql);

            stat.setString(1, tkodetabungan.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
            kosong();
            tkodetabungan.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Dihapus"+e);
        }
    }//GEN-LAST:event_bhapusActionPerformed

