        protected void aktif(){
        tnopinjaman.setEnabled(true);
        ttanggal.setEnabled(true);
        tnik.setEnabled(true);
        tnama.setEnabled(true);
        talamat.setEnabled(true);
        tketerangan.setEnabled(true);
        tjumlahpinjam.setEnabled(true);
        tlamaangsuran.setEnabled(true);
        tbunga.setEnabled(true);
        ttotalpinjam.setEnabled(true);
        tcicilan.setEnabled(true);
        tnopinjaman.requestFocus(true);
    }
            
     protected void kosong(){
         tnopinjaman.setText("");
         ttanggal.setText("");
         tnik.setText("");
         tnama.setText("");
         talamat.setText("");
         tketerangan.setText("");
         tjumlahpinjam.setText("");
         tlamaangsuran.setText("");
         ttotalpinjam.setText("");
         tcicilan.setText("");
     }
     
     public void bunga(){
        try{
	ttotalpinjam.setText(String.valueOf(bunga));
        tcicilan.setText(String.valueOf(total));
     
            int bungahitung = Integer.parseInt(tbunga.getText());
            int jumlahhitung = Integer.parseInt(tjumlahpinjam.getText());
            int angsuranhitung = Integer.parseInt(tlamaangsuran.getText());
            bunga = bungahitung*jumlahhitung/100;
            total = jumlahhitung+bunga; 
            cicilan = total/angsuranhitung;
            
        ttotalpinjam.setText(String.valueOf(total));
        tcicilan.setText(String.valueOf(cicilan));
            
            }catch (Exception e) {
            
        }
            
     }
    
     protected void datatable(){
         Object[] Baris ={"no_pinjaman","tanggal_pinjaman","nik","nama","alamat","keterangan","jumlah_pinjaman","lama_angsuran","bunga","total_pinjaman","cicilan"};
         tableModel = new DefaultTableModel (null, Baris);
         tblpinjaman.setModel(tableModel);
         String sql = "select * from pinjaman";
         try{
            java.sql.Statement stat=conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
             String a = hasil.getString("no_pinjaman");
             String b = hasil.getString("tanggal_pinjaman");
             String c = hasil.getString("nik");
             String d = hasil.getString("nama");
             String e = hasil.getString("alamat");
             String f = hasil.getString("keterangan");
             String g = hasil.getString("jumlah_pinjaman");
             String h = hasil.getString("lama_angsuran");
             String i = hasil.getString("bunga");
             String j = hasil.getString("total_pinjaman");
             String k = hasil.getString("cicilan");
             
             String[] data={a,b,c,d,e,f,g,h,i,j,k};
             tableModel.addRow(data);
            }   
         }catch(Exception e){
         
         }
         
         
   
     }
     
