    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        String sql="insert into pinjaman value(?,?,?,?,?,?,?,?,?,?,?)";
        try{
            PreparedStatement stat=conn.prepareStatement(sql);
            stat.setString(1, tnopinjaman.getText());
            stat.setString(2, ttanggal.getText());
            stat.setString(3, tnik.getText());
            stat.setString(4, tnama.getText());
            stat.setString(5, talamat.getText());
            stat.setString(6, tketerangan.getText());
            stat.setString(7, tjumlahpinjam.getText());
            stat.setString(8, tlamaangsuran.getText());
            stat.setString(9, tbunga.getText());
            stat.setString(10, ttotalpinjam.getText());
            stat.setString(11, tcicilan.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data BERHASIL disimpan");
            kosong();
            tnopinjaman.requestFocus();
            datatable();
            
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data GAGAL disimpan");
        }
    }//GEN-LAST:event_bsimpanActionPerformed

