    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        try{
            String sql="DELETE from pinjaman where no_pinjaman=?";
            PreparedStatement stat= conn.prepareStatement(sql);

            stat.setString(1, tnopinjaman.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
            kosong();
            tnopinjaman.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Dihapus"+e);
        }
    }//GEN-LAST:event_bhapusActionPerformed

    private void bubahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bubahActionPerformed
        try{
            String sql="UPDATE pinjaman set tanggal_pinjaman=?, nik=?, nama=?, alamat=?, keterangan=?, jumlah_pinjaman=?, lama_angsuran=?, bunga=?, total_pinjaman=?, cicilan=? where no_pinjaman=?";
            PreparedStatement stat= conn.prepareStatement(sql);
            
            
            stat.setString(1, ttanggal.getText());
            stat.setString(2, tnik.getText());
            stat.setString(3, tnama.getText());
            stat.setString(4, talamat.getText());
            stat.setString(5, tketerangan.getText());
            stat.setString(6, tjumlahpinjam.getText());
            stat.setString(7, tlamaangsuran.getText());
            stat.setString(8, tbunga.getText());
            stat.setString(9, ttotalpinjam.getText());
            stat.setString(10, tcicilan.getText());
            stat.setString(11, tnopinjaman.getText());
            
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            tnopinjaman.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }
    }//GEN-LAST:event_bubahActionPerformed

